package com.eoe.BasicLearn.day05;

public class For02 {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for (int a = 65; a <= 90; a++) {
			System.out.println(a + " " + (char) a);
		}
		for (char b = 'A'; b <= 'Z'; b++) {
			System.out.println(b + " " + (int) b);
		}
	}
}
